title 'Test GCP google_logging_folder_exclusion resource.'

folder_exclusion = attribute('folder_exclusion', default: {"name"=>"inspec-folder-exclusion", "description"=>"My folder exclusion description", "filter"=>"resource.type = gce_instance AND severity <= DEBUG"})
gcp_organization_id = attribute(:gcp_organization_id, default: gcp_organization_id, description: 'The identifier of the organization that is the parent of the folder')
gcp_enable_privileged_resources = attribute(:gcp_enable_privileged_resources, default:0, description:'Flag to enable privileged resources requiring elevated privileges in GCP.')
control 'google_logging_folder_exclusion-1.0' do
  impact 1.0
  title 'google_logging_folder_exclusion resource test'

  only_if { gcp_enable_privileged_resources.to_i == 1 && gcp_organization_id != '' }
  # Getting folder exclusions is complicated due to the name being generated by the server.
  # This can be drastically simplified if you have the name when writing the test
  describe.one do
    google_resourcemanager_folders(parent: "organizations/#{gcp_organization_id}").names.each do |folder_name|
      # name on a folder is in the form `folders/12345`
      google_logging_folder_exclusions(folder: folder_name.split('/')[1]).names.each do |exclusion_name|
        describe google_logging_folder_exclusion(folder: folder_name.split('/')[1], name: exclusion_name) do
          its('name'){ should cmp folder_exclusion['name'] }
          its('description'){ should cmp folder_exclusion['description'] }
          its('filter'){ should cmp folder_exclusion['filter'] }
        end
      end
    end
  end
end
